import React, { useEffect, useRef } from 'react';
import DropdownMenu from './DropdownMenu';
import HomeButton from './HomeButton';
import DiscordLoginButton from './DiscordLoginButton';
import './HorizontalMenu.css';

const HorizontalMenu = () => {
    const navRef = useRef(null);

    useEffect(() => {
        const handleScroll = () => {
            const menu = navRef.current;
            const scrollY = window.scrollY;

            // Ajusta estos valores para controlar el efecto
            const startBlurAt = 10; // Comenzar efecto a los 50px de scroll
            const maxBlurAt = 40; // M치ximo blur a los 200px
            const maxBlur = 500; // Cantidad m치xima de blur

            let blurProgress = Math.min((scrollY - startBlurAt) / (maxBlurAt - startBlurAt), 1);
            blurProgress = Math.max(blurProgress, 0);

            if (menu) {
                const currentBlur = blurProgress * maxBlur;
                const bgOpacity = blurProgress * 0.7; // Opacidad del fondo

                menu.style.cssText = `
                    backdrop-filter: blur(${currentBlur}px);
                    -webkit-backdrop-filter: blur(${currentBlur}px);
                    background-color: rgba(26, 26, 46, ${bgOpacity});
                    border-color: rgba(255, 0, 0, ${blurProgress * 0.5});
                `;
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);


    return (
        <nav className="horizontal-menu" ref={navRef}>
            <ul>
                <li className="home-button">
                    <HomeButton />
                </li>
                <li className="dropdown-menu">
                    <DropdownMenu />
                </li>
                <li className="discord-login">
                    <DiscordLoginButton />
                </li>
            </ul>
        </nav>
    );
};

export default HorizontalMenu;
