import React, { useEffect, useRef } from 'react';
import DropdownMenu from './DropdownMenu';
import HomeButton from './HomeButton';
import DiscordLoginButton from './DiscordLoginButton';
import './HorizontalMenu.css';

const HorizontalMenu = () => {
    const navRef = useRef(null);

    // En tu componente HorizontalMenu.jsx
    useEffect(() => {
        const handleScroll = () => {
            const menu = navRef.current;
            const scrollY = window.scrollY;
            const maxBlur = 10; // Máximo blur (10px)
            const blurStart = 100; // Píxeles scroll donde inicia el efecto
            const blurEnd = 300; // Píxeles scroll donde alcanza el máximo

            // Calcula el progreso del scroll
            let blurProgress = Math.min((scrollY - blurStart) / (blurEnd - blurStart), 1);
            blurProgress = Math.max(blurProgress, 0);

            if (menu) {
                if (scrollY > blurStart) {
                    const currentBlur = blurProgress * maxBlur;
                    const bgOpacity = blurProgress * 0.85;

                    menu.style.backdropFilter = `blur(${currentBlur}px)`;
                    menu.style.webkitBackdropFilter = `blur(${currentBlur}px)`;
                    menu.style.backgroundColor = `rgba(26, 26, 46, ${bgOpacity})`;
                } else {
                    menu.style.backdropFilter = 'blur(0)';
                    menu.style.webkitBackdropFilter = 'blur(0)';
                    menu.style.backgroundColor = 'rgba(26, 26, 46, 0)';
                }
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <nav className="horizontal-menu" ref={navRef}>
            <ul>
                <li className="home-button">
                    <HomeButton />
                </li>
                <li className="dropdown-menu">
                    <DropdownMenu />
                </li>
                <li className="discord-login">
                    <DiscordLoginButton />
                </li>
            </ul>
        </nav>
    );
};

export default HorizontalMenu;
