import React, { useEffect, useRef } from 'react';
import DropdownMenu from './DropdownMenu';
import HomeButton from './HomeButton';
import DiscordLoginButton from './DiscordLoginButton';
import './HorizontalMenu.css';

const HorizontalMenu = () => {
    const navRef = useRef(null);

    useEffect(() => {
        let animationFrameId;

        const handleScroll = () => {
            if (animationFrameId) return;

            animationFrameId = requestAnimationFrame(() => {
                const menu = navRef.current;
                const scrollY = window.scrollY;
                const maxBlur = 5;
                const blurStart = 100;
                const blurEnd = 300;

                let blurProgress = Math.min((scrollY - blurStart) / (blurEnd - blurStart), 1);
                blurProgress = Math.max(blurProgress, 0);

                if (menu) {
                    const currentBlur = blurProgress * maxBlur;
                    const bgOpacity = blurProgress * 0.85;

                    // Aplicar estilos con prefijos
                    menu.style.cssText = `
                        backdrop-filter: blur(${currentBlur}px);
                        -webkit-backdrop-filter: blur(${currentBlur}px);
                        background-color: rgba(26, 26, 46, ${bgOpacity});
                    `;
                }
                animationFrameId = null;
            });
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
            if (animationFrameId) cancelAnimationFrame(animationFrameId);
        };
    }, []);

    return (
        <nav className="horizontal-menu" ref={navRef}>
            <ul>
                <li className="home-button">
                    <HomeButton />
                </li>
                <li className="dropdown-menu">
                    <DropdownMenu />
                </li>
                <li className="discord-login">
                    <DiscordLoginButton />
                </li>
            </ul>
        </nav>
    );
};

export default HorizontalMenu;
