import React, { useEffect, useRef } from 'react';
import DropdownMenu from './DropdownMenu';
import HomeButton from './HomeButton';
import DiscordLoginButton from './DiscordLoginButton';
import './HorizontalMenu.css';

const HorizontalMenu = () => {
    const navRef = useRef(null);

    useEffect(() => {
        const handleScroll = () => {
            const menu = navRef.current;
            const scrollY = window.scrollY;

            // Declarar blurAmount correctamente
            const blurAmount = Math.min(scrollY * 0.1, 15); // <-- Declaración

            menu.style.backdropFilter = `blur(${blurAmount}px)`;
            menu.style.webkitBackdropFilter = `blur(${blurAmount}px)`;
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);


    return (
        <nav className="horizontal-menu" ref={navRef}>
            <ul>
                <li className="home-button">
                    <HomeButton />
                </li>
                <li className="dropdown-menu">
                    <DropdownMenu />
                </li>
                <li className="discord-login">
                    <DiscordLoginButton />
                </li>
            </ul>
        </nav>
    );
};

export default HorizontalMenu;
