import React, { useEffect, useRef } from 'react';
import DropdownMenu from './DropdownMenu';
import HomeButton from './HomeButton';
import DiscordLoginButton from './DiscordLoginButton';
import './HorizontalMenu.css';

const HorizontalMenu = () => {
    const navRef = useRef(null);

    useEffect(() => {
        const handleScroll = () => {
            const menu = navRef.current;
            const scrollThreshold = 10; // Píxeles a desplazar para aplicar efecto completo
            const scrollY = window.scrollY || window.pageYOffset;

            // Calcula la progresión del efecto (0 a 1)
            const blurProgress = Math.min(scrollY / scrollThreshold, 1);

            if (menu) {
                // Aplica blur dinámico
                const currentBlur = blurProgress * 8; // 8px máximo
                const bgOpacity = blurProgress * 0.9;

                menu.style.backdropFilter = `blur(${currentBlur}px)`;
                menu.style.webkitBackdropFilter = `blur(${currentBlur}px)`;
                menu.style.backgroundColor = `rgba(26, 26, 46, ${bgOpacity})`;

                // Agrega/remueve clase para transiciones suaves
                if (scrollY > 10) {
                    menu.classList.add('scrolled');
                } else {
                    menu.classList.remove('scrolled');
                }
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);


    return (
        <nav className="horizontal-menu" ref={navRef}>
            <ul>
                <li className="home-button">
                    <HomeButton />
                </li>
                <li className="dropdown-menu">
                    <DropdownMenu />
                </li>
                <li className="discord-login">
                    <DiscordLoginButton />
                </li>
            </ul>
        </nav>
    );
};

export default HorizontalMenu;
